    if (!contactForm.name || !contactForm.email) return;
    setSending(true);
    try {
      await fetch("https://formspree.io/f/mlgwzypa", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...contactForm, _subject: `New contact from ${contactForm.name}  ${contactForm.company}` }),
      });
    } catch (e) { /* still show success */ }
    setSending(false);
    setFormSent(true);
