//  CHECKOUT PAGE 
function CheckoutPage({ cartItem, setPage, payMethod, setPayMethod, checkoutForm, setCheckoutForm, addons }) {
  const [errors, setErrors] = useState({});
  const [attempted, setAttempted] = useState(false);
  const form = checkoutForm;
  const setForm = setCheckoutForm;

  const validate = () => {
    const e = {};
    if (!form.name.trim()) e.name = "Name is required";
    if (!form.company.trim()) e.company = "Company is required";
    if (!form.email.trim()) e.email = "Email is required";
    else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(form.email)) e.email = "Please enter a valid email";
    return e;
  };

  const handleSubmit = async () => {
    setAttempted(true);
    const e = validate();
    setErrors(e);
    if (Object.keys(e).length === 0) {
      try {
        await fetch("https://formspree.io/f/mlgwzypa", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            ...form,
            package: cartItem?.name || "Unknown",
            paymentMethod: payMethod,
            addons: addons.join(", ") || "None",
            _subject: ` New Hire Request from ${form.name}  ${form.company} (${cartItem?.name})`,
          }),
        });
      } catch (err) { /* still proceed */ }
      track("checkout_complete", {
        package_name: cartItem?.name,
        payment_method: payMethod,
        addons: addons.join(", ") || "none",
        company: form.company,
      });
      setPage("confirm");
      window.scrollTo(0, 0);
    }
  };

  useEffect(() => {
    if (attempted) setErrors(validate());
  }, [form]);

  const hasErrors = Object.keys(errors).length > 0;

  return (
    <div className="fade-in">
      <div className="section">
        <h2 style={{ fontSize: 36, fontWeight: 800, marginBottom: 32, letterSpacing: -1 }}>Checkout</h2>
        <div className="checkout-grid">
          <div>
            <div style={{ fontSize: 20, fontWeight: 700, marginBottom: 20 }}>Your Details</div>
            <div className="checkout-row">
              <div className="checkout-form-group">
                <label className="checkout-label">Full Name *</label>
                <input className={`checkout-input${errors.name ? " error" : ""}`} placeholder="Jane Smith" value={form.name} onChange={e => setForm({...form, name: e.target.value})} />
                {errors.name && <div className="field-error">{errors.name}</div>}
              </div>
              <div className="checkout-form-group">
                <label className="checkout-label">Company *</label>
                <input className={`checkout-input${errors.company ? " error" : ""}`} placeholder="Acme Corp" value={form.company} onChange={e => setForm({...form, company: e.target.value})} />
                {errors.company && <div className="field-error">{errors.company}</div>}
              </div>
            </div>
            <div className="checkout-row">
              <div className="checkout-form-group">
                <label className="checkout-label">Email *</label>
                <input className={`checkout-input${errors.email ? " error" : ""}`} type="email" placeholder="team@gmail.com" value={form.email} onChange={e => setForm({...form, email: e.target.value})} />
                {errors.email && <div className="field-error">{errors.email}</div>}
              </div>
              <div className="checkout-form-group">
                <label className="checkout-label">Phone (Optional)</label>
                <input className="checkout-input" placeholder="+61 4XX XXX XXX" value={form.phone} onChange={e => setForm({...form, phone: e.target.value})} />
              </div>
            </div>
            <div className="checkout-form-group">
              <label className="checkout-label">Message (Optional)</label>
              <textarea className="checkout-textarea" placeholder="Tell me about the role..." value={form.message} onChange={e => setForm({...form, message: e.target.value})} />
            </div>
            <div style={{ fontSize: 20, fontWeight: 700, marginBottom: 4, marginTop: 20 }}>Payment Method</div>
            <div className="payment-options">
              {[
                { id: "interview", icon: Icons.chat, name: "Interview", desc: "30-min video call" },
                { id: "coffee", icon: Icons.coffee, name: "Coffee Chat", desc: "Meet in person" },
                { id: "trial", icon: Icons.briefcase, name: "Trial Day", desc: "See me in action" },
              ].map(pm => (
                <div key={pm.id} className={`payment-card${payMethod === pm.id ? " selected" : ""}`} onClick={() => { setPayMethod(pm.id); track("select_payment_method", { method: pm.id }); }}><div className="payment-icon">{pm.icon}</div><div className="payment-name">{pm.name}</div><div className="payment-desc">{pm.desc}</div></div>
              ))}
            </div>
          </div>
          <div className="cart-summary">
            <div className="cart-summary-title">Order Summary</div>
            <div style={{ display: "flex", gap: 12, alignItems: "center", marginBottom: 16 }}>
              <div style={{ width: 56, height: 56, borderRadius: 12, overflow: "hidden", flexShrink: 0 }}><img src={PHOTO_SRC} alt="Antoine" style={{ width: "100%", height: "100%", objectFit: "cover" }} /></div>
              <div><div style={{ fontWeight: 700 }}>Antoine Racon</div><div style={{ fontSize: 13, color: "var(--text-secondary)" }}>{cartItem?.name || "eCommerce Specialist"}  Permanent Role</div></div>
            </div>
            <div className="cart-summary-divider" />
            <div className="cart-summary-total"><span>Total</span><span>One Great Hire</span></div>
            <button className="btn btn-primary" style={{ width: "100%", justifyContent: "center" }} onClick={handleSubmit} disabled={attempted && hasErrors}>
              Complete Hiring
            </button>
            <div style={{ textAlign: "center", fontSize: 13, color: "var(--text-tertiary)", marginTop: 12, fontStyle: "italic" }}>By completing, you agree to receive one extremely motivated employee.</div>
          </div>
        </div>
      </div>
    </div>
  );
}

