//  APP 
export default function App() {
  const [page, setPage] = useState("product");
  const [cartItem, setCartItem] = useState(null);
  const [toast, setToast] = useState(null);
  const [miniCartOpen, setMiniCartOpen] = useState(false);
  const [addons, setAddons] = useState([]);
  const [payMethod, setPayMethod] = useState("interview");
  const [checkoutForm, setCheckoutForm] = useState({ name: "", company: "", email: "", phone: "", message: "" });

  const showToast = (msg) => {
    setToast(null);
    setTimeout(() => setToast(msg), 10);
  };

  const addToCartAndOpen = (pkg) => {
    setCartItem(pkg);
    setMiniCartOpen(true);
    showToast(`"${pkg.name}" added to cart`);
    track("add_to_cart", { package_name: pkg.name, package_price: pkg.price });
  };

  return (
    <>
      <style>{css}</style>
      <Nav page={page} setPage={setPage} cartItem={cartItem} setCartItem={setCartItem} miniCartOpen={miniCartOpen} setMiniCartOpen={setMiniCartOpen} />
      {page === "product" && <ProductPage setPage={setPage} cartItem={cartItem} setCartItem={setCartItem} showToast={showToast} addToCartAndOpen={addToCartAndOpen} />}
      {page === "contact" && <ContactPage />}
      {page === "cart" && <CartPage cartItem={cartItem} setCartItem={setCartItem} setPage={setPage} addons={addons} setAddons={setAddons} />}
      {page === "checkout" && <CheckoutPage cartItem={cartItem} setPage={setPage} payMethod={payMethod} setPayMethod={setPayMethod} checkoutForm={checkoutForm} setCheckoutForm={setCheckoutForm} addons={addons} />}
      {page === "confirm" && <ConfirmPage cartItem={cartItem} setPage={setPage} setCartItem={setCartItem} addons={addons} payMethod={payMethod} checkoutForm={checkoutForm} setAddons={setAddons} />}
      <footer className="footer"><div> 2026 Antoine Racon  Built with ambition  Sydney / Melbourne</div></footer>
      {toast && <Toast message={toast} onDone={() => setToast(null)} />}
    </>
  );
}
